mapper.select:
SELECT sum(cnt) AS total, substr(start_date, 5, 2) as start_date FROM ( select count(1) as cnt, to_char(a.log_dt, 'yyyymm') as start_date from mng_log_menu_info a, mngr_menu_mng b where 1=1
and to_char(a.log_dt, 'YYYYMM') >= date_trunc('month', to_date(#{startDate}, 'yyyymmdd')::date - interval '6 month')::text and to_char(a.log_dt, 'YYYYMM') <= #{startDate}
and a.lwrt_menu_cd = b.lwrt_menu_cd and a.lwrt_menu_cd in('MENU_00001', 'MENU_00004', 'MENU_00005', 'MENU_00050', 'MENU_00057', 'MENU_00051', 'MENU_00056', 'MENU_00052', 'MENU_00053', 'MENU_00058') group by a.lwrt_menu_cd, a.lwrt_menu_nm, b.menu_link, a.log_dt order by cnt desc, a.lwrt_menu_cd, a.lwrt_menu_nm ) a GROUP BY start_date

SELECT b.date as start_date, CASE WHEN a.cnt IS NULL THEN 0 ELSE a.cnt END AS total FROM ( select case when EXTRACT(DOW FROM a.log_dt) = 0 then '일요일' when EXTRACT(DOW FROM a.log_dt) = 1 then '월요일' when EXTRACT(DOW FROM a.log_dt) = 2 then '화요일' when EXTRACT(DOW FROM a.log_dt) = 3 then '수요일' when EXTRACT(DOW FROM a.log_dt) = 4 then '목요일' when EXTRACT(DOW FROM a.log_dt) = 5 then '금요일' else '토요일' end as start_date, count(1) as cnt from mng_log_menu_info a, mngr_menu_mng b where 1=1
and to_char(a.log_dt, 'YYYYMM') <= #{startDate}
and a.lwrt_menu_cd = b.lwrt_menu_cd and a.lwrt_menu_cd in('MENU_00001', 'MENU_00004', 'MENU_00005', 'MENU_00050', 'MENU_00057', 'MENU_00051', 'MENU_00056', 'MENU_00052', 'MENU_00053', 'MENU_00058') group by EXTRACT(DOW FROM a.log_dt) ) a right join (select regexp_split_to_table('일요일,월요일,화요일,수요일,목요일,금요일,토요일', ',') as date, 0 as cnt)b on b.date = a.start_date order by a.start_date = '토요일', a.start_date = '금요일', a.start_date = '목요일', a.start_date = '수요일', a.start_date = '화요일', a.start_date = '월요일', a.start_date = '일요일'

select a.page_cnt, b.visitor, c.visitor_cnt, substr(a.start_date, 5, 2) as start_date from ( select to_char(log_dt, 'yyyymm') as start_date, count(1) as page_cnt from mng_log_menu_info a where 1=1
and to_char(a.log_dt, 'YYYYMM') >= date_trunc('month', to_date(#{startDate}, 'yyyymmdd')::date - interval '6 month')::text and to_char(a.log_dt, 'YYYYMM') <= #{startDate}
group by to_char(a.log_dt, 'yyyymm') ) a, ( select sum(cnt) as visitor, start_date from ( select count(DISTINCT user_id) as cnt , to_char(LOG_DT, 'yyyymm') as start_date from mng_log_user_info where 1=1 group by user_id, to_char(LOG_DT, 'yyyymmdd'), to_char(LOG_DT, 'yyyymm') ) a group by start_date ) b, ( select sum(cnt) as visitor_cnt, start_date from ( select count(user_id) as cnt , to_char(LOG_DT, 'yyyymm') as start_date from mng_log_user_info where 1=1 group by user_id, to_char(LOG_DT, 'yyyymm') ) a group by start_date ) c where 1=1 and a.start_date = b.start_date and b.start_date = c.start_date group by a.start_date, b.visitor, c.visitor_cnt, a.page_cnt

select a.page_cnt, b.visitor, c.visitor_cnt, a.start_date from ( select b.date as start_date, case when a.page_cnt is null then 0 else a.page_cnt end as page_cnt from ( select case when EXTRACT(DOW FROM a.log_dt) = 0 then '일요일' when EXTRACT(DOW FROM a.log_dt) = 1 then '월요일' when EXTRACT(DOW FROM a.log_dt) = 2 then '화요일' when EXTRACT(DOW FROM a.log_dt) = 3 then '수요일' when EXTRACT(DOW FROM a.log_dt) = 4 then '목요일' when EXTRACT(DOW FROM a.log_dt) = 5 then '금요일' else '토요일' end as start_date, count(1) as page_cnt from mng_log_menu_info a where 1=1 and to_char(a.log_dt, 'YYYYMM') = #{startDate}
and a.user_nm like #{searchServiceUserInfo}||'%'
and a.lwrt_menu_cd != 'MENU_00002' and a.menu_cd not in('MENU_00003', 'MENU_00006', 'MENU_00007', 'MENU_00008', 'MENU_00009', 'MENU_00010', 'MENU_00011', 'MENU_00012', 'MENU_00013', 'MENU_00014', 'MENU_00015', 'MENU_00016', 'MENU_00017', 'MENU_00018', 'MENU_00019') group by EXTRACT(DOW FROM a.log_dt) ) a right join (select regexp_split_to_table('일요일,월요일,화요일,수요일,목요일,금요일,토요일', ',') as date, 0 as cnt) b on b.date = a.start_date ) a , ( select b.date as start_date, case when a.visitor is null then 0 else a.visitor end as visitor from ( select count(DISTINCT user_id) as visitor , case when EXTRACT(DOW FROM log_dt) = 0 then '일요일' when EXTRACT(DOW FROM log_dt) = 1 then '월요일' when EXTRACT(DOW FROM log_dt) = 2 then '화요일' when EXTRACT(DOW FROM log_dt) = 3 then '수요일' when EXTRACT(DOW FROM log_dt) = 4 then '목요일' when EXTRACT(DOW FROM log_dt) = 5 then '금요일' else '토요일' end as start_date from mng_log_user_info where 1=1 and to_char(log_dt, 'YYYYMM') = #{startDate}
and a.user_nm like #{searchServiceUserInfo}||'%'
group by EXTRACT(DOW FROM log_dt) ) a right join (select regexp_split_to_table('일요일,월요일,화요일,수요일,목요일,금요일,토요일', ',') as date, 0 as cnt) b on b.date = a.start_date ) b, ( select b.date as start_date, case when a.visitor_cnt is null then 0 else a.visitor_cnt end as visitor_cnt from ( select count(user_id) as visitor_cnt , case when EXTRACT(DOW FROM log_dt) = 0 then '일요일' when EXTRACT(DOW FROM log_dt) = 1 then '월요일' when EXTRACT(DOW FROM log_dt) = 2 then '화요일' when EXTRACT(DOW FROM log_dt) = 3 then '수요일' when EXTRACT(DOW FROM log_dt) = 4 then '목요일' when EXTRACT(DOW FROM log_dt) = 5 then '금요일' else '토요일' end as start_date from mng_log_user_info where 1=1 and to_char(log_dt, 'YYYYMM') = #{startDate}
and user_nm like #{searchServiceUserInfo}||'%'
group by EXTRACT(DOW FROM log_dt) ) a right join (select regexp_split_to_table('일요일,월요일,화요일,수요일,목요일,금요일,토요일', ',') as date, 0 as cnt) b on b.date = a.start_date ) c where 1=1 and a.start_date = b.start_date and b.start_date = c.start_date order by a.start_date = '토요일', a.start_date = '금요일', a.start_date = '목요일', a.start_date = '수요일', a.start_date = '화요일', a.start_date = '월요일', a.start_date = '일요일'
